    <div className="App">
      <div className="app-container">
        {/* 
            This is your Create Card component.
          */}
        <Card>
          <h2>Create Todo</h2>
          <form>
            <Input onChange={() => {}} placeholder="Title" type="text" />
            <TextArea onChange={() => {}} placeholder="Description" />
            <Button type="submit">Create</Button>
          </form>
        </Card>

        {/* 
          My Todos
        */}
        <Card>
          <h1>My todos</h1>
          <Button onClick={() => console.log("Open Modal")}>Add +</Button>
          <div className="list-container">
            <TodoItem id={todoList[0].id} title={todoList[0].title} description={todoList[0].description} completed={todoList[0].completed} />
           
            {
              todoList
              .filter((item) => item.completed === false )
              .map((item) => (
                <TodoItem key={item.id} id={item.id} title={item.title} description={item.description} completed={item.completed} />
              ))
            }

          </div>

          <div className="separator"></div>

          <h2>Completed</h2>
          <div className="list-container">
            <TodoItem completed={true} />
            <TodoItem completed={true} />
          </div>
        </Card>
      </div>
    </div>
  );
}

export default App;
